import {Component, OnInit, ViewEncapsulation} from '@angular/core';
import {ActivatedRoute} from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  encapsulation: ViewEncapsulation.None
})
export class HomeComponent implements OnInit {

  constructor(
    private route: ActivatedRoute) {
  }

  needsLogin: boolean | undefined;
  _userName: string = '';

  ngOnInit() {
    this.needsLogin = !!this.route.snapshot.params['needsLogin'];
  }

  get userName(): string {
    return this._userName;
  }

  login(): void {
    this._userName = 'Max';
  }

  logout(): void {
    this._userName = '';
  }


}
<div class="card">
  <div class="header">
    <h1 class="title">Bienvenido {{userName}}</h1>
  </div>
  <div class="content">

    <div *ngIf="needsLogin" class="alert alert-warning">
      Has sido redirigido a esta página porque no tienes los datos necesarios
      derechos para la acción solicitada. ¡Inicie sesión con una cuenta de usuario respectiva!
    </div>

    <button (click)="login()" class="btn btn-default">Logear</button> <!-- modificamo el texto a español-->
    <button (click)="logout()" class="btn btn-default">Cerrar Seccion</button>

    
    <tabbed-pane [activeId]="0">
        <tab title="Booked" [id]="0">
            This page could display all booked passengers ...
        </tab>
        <tab title="Checked in" [id]="1">
            This page could display all checked in passengers ...
        </tab>

        <tab title="Boarded" [id]="2">
            This page could display all boarded passengers ...
        </tab>
    </tabbed-pane>
    -->
  </div>

</div>
